mapper.select:
select t.id, u.name as owner, t.money, t.name, t.expectedDate, cus.name as customerId, t.stage, t.type, t.source, a.name as activityId, con.fullname as contactsId, t.createBy, t.createTime, t.editBy, t.editTime, t.description, t.contactSummary, t.nextContactTime from tbl_tran t left join tbl_user u on u.id = t.owner left join tbl_customer cus on cus.id = t.customerId left join tbl_activity a on a.id = t.activityId left join tbl_contacts con on con.id = t.contactsId
u.name like '%' #{owner} '%'
and t.name like '%' #{name} '%'
and t.stage=#{stage}
and t.type=#{type}
and t.source=#{source}
and customerId in ( select id from tbl_customer where name like '%' #{customerId} '%' )
and contactsId in ( select id from tbl_contacts where fullname like '%' #{contactsId} '%' )
order by t.createTime desc limit #{pageCount},#{pageSize}

select count(*) from tbl_tran t join tbl_user u on u.id = t.owner join tbl_customer cus on cus.id = t.customerId join tbl_activity a on a.id = t.activityId join tbl_contacts con on con.id = t.contactsId
u.name like '%' #{owner} '%'
and t.name like '%' #{name} '%'
and t.stage=#{stage}
and t.type=#{type}
and t.source=#{source}
and customerId in ( select id from tbl_customer where name like '%' #{customerId} '%' )
and contactsId in ( select id from tbl_contacts where fullname like '%' #{contactsId} '%' )
